import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import ProductCard from "../components/ProductCard";
import productsData from "../data/bs.json";

function Home() {
  const { isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFeaturedProducts = async () => {
      try {
        const res = await api.get("/products");
        // Mostrar solo los primeros 4 productos como destacados
        setFeaturedProducts(res.data.slice(0, 4));
      } catch (err) {
        console.error("Error al cargar productos destacados:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchFeaturedProducts();
  }, []);

  const categories = [
    { name: "Electr√≥nicos", icon: "üì±", color: "bg-blue-100", link: "/products?category=electronicos" },
    { name: "Ropa", icon: "üëï", color: "bg-pink-100", link: "/products?category=ropa" },
    { name: "Hogar", icon: "üè†", color: "bg-green-100", link: "/products?category=hogar" },
    { name: "Deportes", icon: "‚öΩ", color: "bg-orange-100", link: "/products?category=deportes" },
    { name: "Libros", icon: "üìö", color: "bg-purple-100", link: "/products?category=libros" },
    { name: "Belleza", icon: "üíÑ", color: "bg-red-100", link: "/products?category=belleza" }
  ];

  const benefits = [
    {
      icon: "üöö",
      title: "Env√≠o gratis",
      description: "En compras superiores a $25.000"
    },
    {
      icon: "üîí",
      title: "Compra protegida",
      description: "Recib√≠ el producto que esperabas o te devolvemos tu dinero"
    },
    {
      icon: "üí≥",
      title: "Pag√° como quieras",
      description: "Tarjeta de cr√©dito, d√©bito, efectivo o cuotas sin inter√©s"
    },
    {
      icon: "üìû",
      title: "Soporte 24/7",
      description: "Estamos aqu√≠ para ayudarte cuando lo necesites"
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <section className="bg-gradient-to-r from-primary to-primary-dark text-white py-16">
        <div className="max-w-7xl mx-auto px-4">
          <div className="text-center">
            <h1 className="text-5xl font-bold mb-6">
              Compr√° y vend√© todo lo que necesit√°s
            </h1>
            <p className="text-xl mb-8 text-blue-100">
