const PaymentModal = ({ onClose, onSave }) => {
  const [paymentType, setPaymentType] = useState("card");
  const [formData, setFormData] = useState({
    holderName: "",
    cardNumber: "",
    expiryMonth: "",
    expiryYear: "",
    cvv: "",
    email: ""
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (paymentType === "card") {
      onSave({
        type: "card",
        brand: "Visa", // Simplificado
        lastFour: formData.cardNumber.slice(-4),
        expiryMonth: formData.expiryMonth,
        expiryYear: formData.expiryYear,
        holderName: formData.holderName
      });
    } else {
      onSave({
        type: "paypal",
        email: formData.email
      });
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 w-full max-w-md">
        <h3 className="text-lg font-bold mb-4">Agregar Método de Pago</h3>

        <div className="flex space-x-4 mb-4">
          <button
            onClick={() => setPaymentType("card")}
            className={`flex-1 py-2 px-4 rounded-lg ${paymentType === "card" ? "bg-blue-600 text-white" : "bg-gray-200"}`}
          >
            Tarjeta
          </button>
          <button
            onClick={() => setPaymentType("paypal")}
            className={`flex-1 py-2 px-4 rounded-lg ${paymentType === "paypal" ? "bg-blue-600 text-white" : "bg-gray-200"}`}
          >
            PayPal
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          {paymentType === "card" ? (
            <div className="space-y-4">
              <input
                type="text"
                placeholder="Nombre del titular"
                value={formData.holderName}
                onChange={(e) => setFormData({ ...formData, holderName: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                required
              />
              <input
                type="text"
                placeholder="Número de tarjeta"
                value={formData.cardNumber}
                onChange={(e) => setFormData({ ...formData, cardNumber: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                required
              />
              <div className="grid grid-cols-3 gap-2">
                <input
                  type="text"
                  placeholder="MM"
                  value={formData.expiryMonth}
                  onChange={(e) => setFormData({ ...formData, expiryMonth: e.target.value })}
                  className="px-3 py-2 border border-gray-300 rounded-lg"
                  required
                />
                <input
                  type="text"
                  placeholder="YYYY"
                  value={formData.expiryYear}
                  onChange={(e) => setFormData({ ...formData, expiryYear: e.target.value })}
                  className="px-3 py-2 border border-gray-300 rounded-lg"
                  required
                />
                <input
                  type="text"
                  placeholder="CVV"
                  value={formData.cvv}
                  onChange={(e) => setFormData({ ...formData, cvv: e.target.value })}
                  className="px-3 py-2 border border-gray-300 rounded-lg"
                  required
                />
              </div>
            </div>
          ) : (
            <input
              type="email"
              placeholder="Email de PayPal"
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg"
              required
            />
          )}

          <div className="flex space-x-4 mt-6">
            <button
              type="submit"
              className="flex-1 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700"
            >
              Guardar
            </button>
            <button
              type="button"
              onClick={onClose}
              className="flex-1 bg-gray-300 text-gray-700 py-2 px-4 rounded-lg hover:bg-gray-400"
            >
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

