import React, { useState, useEffect } from "react";
import ProductCard from "../components/ProductCard";

const mockProducts = [
  { id: 1, name: "Producto 1", description: "Descripción del producto 1", price: 100, stock: 5 },
  { id: 2, name: "Producto 2", description: "Descripción del producto 2", price: 200, stock: 0 },
];

function Products() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Aquí irá la llamada a la API real
    setProducts(mockProducts);
  }, []);

  const handleAddToCart = (product) => {
    // Lógica para agregar al carrito
    alert(`Agregado: ${product.name}`);
  };

  return (
    <div className="max-w-5xl mx-auto grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 mt-8 bg-white p-6 rounded shadow border border-primary/30">
      {products.map((product) => (
        <ProductCard key={product.id} product={product} onAddToCart={handleAddToCart} />
      ))}
    </div>
  );
}

export default Products;
