import React from "react";

function ProductCard({ product, onAddToCart }) {
  return (
    <div className="bg-white rounded shadow p-4 flex flex-col border border-primary/40">
      <img
        src={product.image || "https://via.placeholder.com/200x200?text=Producto"}
        alt={product.name}
        className="w-full h-48 object-cover rounded mb-2 border border-primary"
      />
      <h2 className="text-lg font-semibold mb-1 text-secondary">{product.name}</h2>
      <p className="text-gray-700 mb-2">{product.description}</p>
      <div className="flex justify-between items-center mt-auto">
        <span className="font-bold text-accent">${product.price}</span>
        <button
          className="bg-primary text-secondary font-semibold px-3 py-1 rounded shadow hover:bg-primary-dark disabled:opacity-50 border border-secondary"
          onClick={() => onAddToCart(product)}
          disabled={product.stock === 0}
        >
          {product.stock === 0 ? "Sin stock" : "Agregar"}
        </button>
      </div>
    </div>
  );
}

export default ProductCard;
