import React, { useState } from "react";

function Login() {
  const [form, setForm] = useState({ email: "", password: "" });
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const validate = () => {
    const errs = {};
    if (!form.email) errs.email = "El email es obligatorio";
    else if (!/^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(form.email)) errs.email = "Email inválido";
    if (!form.password) errs.password = "La contraseña es obligatoria";
    return errs;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const errs = validate();
    setErrors(errs);
    if (Object.keys(errs).length === 0) {
      // Aquí irá la llamada a la API de login
      alert("Login exitoso (simulado)");
    }
  };

  return (
    <div className="max-w-sm mx-auto mt-8 bg-white p-8 rounded shadow border border-primary/30">
      <h1 className="text-2xl font-bold mb-4 text-accent">Iniciar sesión</h1>
      <form className="flex flex-col gap-4" onSubmit={handleSubmit} noValidate>
        <input
          type="email"
          name="email"
          placeholder="Email"
          className={`p-2 border rounded ${errors.email ? "border-red-500" : "border-primary/40"}`}
          value={form.email}
          onChange={handleChange}
        />
        {errors.email && <span className="text-red-500 text-sm">{errors.email}</span>}
        <input
          type="password"
          name="password"
          placeholder="Contraseña"
          className={`p-2 border rounded ${errors.password ? "border-red-500" : "border-primary/40"}`}
          value={form.password}
          onChange={handleChange}
        />
        {errors.password && <span className="text-red-500 text-sm">{errors.password}</span>}
        <button className="bg-primary text-secondary font-semibold py-2 rounded hover:bg-primary-dark border border-secondary">Ingresar</button>
      </form>
    </div>
  );
}

export default Login;
